EXCEPTION_MESSAGE' matches "BeforeDelete" then "BeforeDelete" when 'EXCEPTION_MESSAGE' matches "INSUFFICIENT_ACCESS_ON_CROSS_REFERENCE_ENTITY" then "Access Rights" when 'EXCEPTION_MESSAGE' matches "failed to get force.com org details" then "Authentication Error" when 'EXCEPTION_MESSAGE' matches "No contact roles found for contract" then "Missing contact roles" when 'EXCEPTION_MESSAGE' matches "index out of bounds" then "index out of bounds" when 'EXCEPTION_MESSAGE' matches "Dependent class is invalid" then "Invalid Class" when 'EXCEPTION_MESSAGE' matches "Permission to access" then "Permission Denied" when 'EXCEPTION_MESSAGE' matches "group membership operation already in progress" then "Group Lock" when 'EXCEPTION_MESSAGE' matches "INVALID_CROSS_REFERENCE" then "Wrong ID"  when 'EXCEPTION_MESSAGE' matches "SObject row was retrieved" then "SOQL Field Error" when 'EXCEPTION_MESSAGE' matches "Invalid field" then "Invalid field" when 'EXCEPTION_MESSAGE' matches "Can only throw this exception" then "Exception Error" when 'EXCEPTION_MESSAGE' matches "Invalid Visualforce page parameter" then "Invalid Parameter" when 'EXCEPTION_MESSAGE' matches "Quote to oppty sync failed" then "Quote-Opp Sync Fail" when 'EXCEPTION_MESSAGE' matches "unexpected token" then "unexpected token" when 'EXCEPTION_MESSAGE' matches "SendEmail failed" then "SendEmail failed"  when 'EXCEPTION_MESSAGE' matches "Batchable instance is too big" then "Batch Size Error"  else "Unknown" end as 'Exception Message Detail', coalesce(substr('EXCEPTION_MESSAGE',index_of('EXCEPTION_MESSAGE',":",1)+1,index_of('EXCEPTION_MESSAGE',":",1,2)-index_of('EXCEPTION_MESSAGE',":",1)-1),"Unknown") as 'Class Name';
