all_1.query:
q = load "${Apps.sfdc_internal__AdminAnalytics.Datasets.ApexUnexpectedException.Alias}";
q = filter q by 'EXCEPTION_MESSAGE' matches "{{cell(ExceptionHeatmap_4.selection, 0, "Exception Message Detail").asString()}}";
q = foreach q generate substr('EXCEPTION_MESSAGE',index_of('EXCEPTION_MESSAGE',":",1)+1,index_of('EXCEPTION_MESSAGE',":",1,2)-index_of('EXCEPTION_MESSAGE',":",1)-1) as 'Class Name', 'STACK_TRACE' as 'STACK_TRACE', 'TIMESTAMP_DERIVED' as 'TIMESTAMP_DERIVED';

q = filter q by 'Class Name' in {{coalesce(column(ROW_LOCKS_2.selection, ["Class Name"]),column(ROW_LOCKS_2.result, ["Class Name"])).asString()}};
q = group q by ('STACK_TRACE','TIMESTAMP_DERIVED');
q = foreach q generate 'STACK_TRACE' as 'STACK_TRACE', 'TIMESTAMP_DERIVED' as 'TIMESTAMP_DERIVED';

ROW_LOCKS_2.query:
q = load "${Apps.sfdc_internal__AdminAnalytics.Datasets.ApexUnexpectedException.Alias}";
q = filter q by 'EXCEPTION_MESSAGE' matches "{{cell(ExceptionHeatmap_4.selection, 0, "Exception Message Detail").asString()}}";
q = foreach q generate substr('EXCEPTION_MESSAGE',index_of('EXCEPTION_MESSAGE',":",1)+1,index_of('EXCEPTION_MESSAGE',":",1,2)-index_of('EXCEPTION_MESSAGE',":",1)-1) as 'Class Name';
q = group q by ('Class Name');
q = foreach q generate  coalesce('Class Name',"Unknown") as 'Class Name', count() as 'Error Count';
q = order q by ('Error Count' desc);
q = limit q 10000

